# Initialize a grid for dynamic programming
paths = [[0 for _ in range(4 + 1)] for _ in range(4 + 1)]

# Base case: 1 way to be at the origin
paths[0][0] = 1

# Fill the DP table
for x in range(4 + 1):
    for y in range(4 + 1):
        if x + y == 5:
            paths[x][y] = 0  # No path can cross the line x + y = 5
        else:
            if x > 0:
                paths[x][y] += paths[x - 1][y]
            if y > 0:
                paths[x][y] += paths[x][y - 1]

# Result: Number of distinct paths to reach the point (4, 3)
