label("{\tiny 18}",(27,-4),S);
label("12",(20,6),E);
label("$E$",I,S);
label("{\tiny $H$}",H,SE);
label("$B$",B,SE);
label("$F$",F,NE);
label("$C$",C,NE);
label("$G$",G,SW);
