A=(0,0);
B=(10,0);
C=(5,8.7);
D=(2.5,4.3);
I=(7.5,4.3);
F=(5,0);
O=(5,2.3);
draw(A--B--C--cycle,linewidth(0.7));
draw(A--I,linewidth(0.7));
draw(B--D,linewidth(0.7));
