A = O+dis1*dir(d1);
C = O+dis2*dir(d2);
B = A+dis2*dir(d2);
G = (0,2);
D = G+dis1*dir(d1);
F = G+dis2*dir(d2);
E = D+dis2*dir(d2);
X = A+.5*dis1*dir(d1);
Y = C+.5*dis2*dir(d2);
Z = (0,4);

fill(G--D--E--F--cycle, gray(0.8));
fill(O--F--G--cycle, gray(0.8));
fill(O--D--G--cycle, gray(0.8));
draw(G--D--E--F--cycle);
draw(G--O);
draw(F--C--O--A--D);
draw(A--B--C, dashed);
draw(B--E, dashed);
draw(O--D, dashed);
draw(O--F, dashed);
draw(O--X, Arrow);
draw(O--Y, Arrow);
draw(O--Z, Arrow);

label("$2$", A, SE);
label("$2$", C, W);
label("$2$", G, SW);
label("$O$", O, S);
label("$X$", X, SE);
label("$Z$", Z, W);
label("$Y$", Y, W);
label("$y=x-1$", (O+A)/2, SE, red+fontsize(10));
label("$y=x+1$", (O+C)/2, SW, red+fontsize(10));

draw((G+D)/2--(E+D)/2, red);
draw((G+F)/2--(E+F)/2, red);
draw((O+C)/2--(C+B)/2, red+dashed);
draw((O+A)/2--(A+B)/2, red+dashed);
draw((O+C)/2--(G+F)/2, red);
draw((C+B)/2--(E+F)/2, red+dashed);
draw((O+A)/2--(G+D)/2, red);
