pair A, B, C, D, E, F, I, O;

A = (5^2/13,5*12/13);
B = (0,0);
C = (13,0);
I = incenter(A,B,C);
D = (I + reflect(B,C)*(I))/2;
E = (I + reflect(C,A)*(I))/2;
F = (I + reflect(A,B)*(I))/2;
O = (B + C)/2;

