int i, j;

draw((0,-1.5)--(0,4.5));
draw((-1.5,0)--(4.5,0));
draw(arc((0,0),4,-20,110));
draw(arc((4,0),4,85,200));
draw(arc((0,4),4,5,-110));
draw(Circle((1,1),0.15),red);
draw(Circle((2,1),0.15),red);
