draw((0,0)--(6,0));
draw((0,0)--(0,6));
filldraw((0,0)--(0,1)--(1,1)--(1,0)--cycle,gray(0.7));
filldraw((1,0)--(1,2)--(2,2)--(2,0)--cycle,gray(0.7));
