filldraw((5,0)--(5,6)--(6,6)--(6,0)--cycle,gray(0.7));

label("$0$", (0,0), S, fontsize(10));
label("$0.01$", (1,0), S, fontsize(10));
label("$0.02$", (2,0), S, fontsize(10));
label("$0.03$", (3,0), S, fontsize(10));
label("$0.99$", (5,0), S, fontsize(10));
